import React, { useState } from 'react';
import EditorCanvas from '../components/editor-canvas';
import Toolbox from '../components/toolbox';

const Editor: React.FC = () => {
  const [elements, setElements] = useState<{ id: string; type: string; content: React.ReactNode; position: { x: number; y: number } }[]>([]);
  const [selectedElement, setSelectedElement] = useState<string | null>(null);

  const handleElementDrop = (type: string) => {
    // Add the dropped element to the canvas with initial position (0, 0)
    const newElement = {
      id: `${type}-${elements.length + 1}`,
      type: type,
      content: `New ${type}`,
      position: { x: 0, y: 0 }
    };
    setElements(prevElements => [...prevElements, newElement]);
  };

  const handleElementClick = (id: string) => {
    // Select the clicked element
    setSelectedElement(id);
  };

  return (
    <div style={{ display: 'flex' }}>
      <Toolbox elements={[{ id: '1', type: 'BUTTON', content: 'Button' }, { id: '2', type: 'IMAGE', content: 'Image' }]} onElementDrop={handleElementDrop} />
      <EditorCanvas elements={elements} gridSize={10} />
    </div>
  );
};

export default Editor;
