// src/components/editor/editor-canvas.tsx

import React from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import DropTarget from './drop-target'; // Import the DropTarget component
import DraggableElement from './drop-target'; // Import the DraggableElement component

interface EditorCanvasProps {
  elements: { type: string; content: React.ReactNode }[]; // Elements to render on the canvas
  onElementDrop: (type: string) => void; // Callback for dropped element
}

const EditorCanvas: React.FC<EditorCanvasProps> = ({ elements, onElementDrop }) => {
  return (
    <DndProvider backend={HTML5Backend}>
      <DropTarget onDrop={onElementDrop}> {/* Use DropTarget as the drop area */}
        <div style={{ width: '100%', height: '500px', border: '1px solid #ddd', padding: '20px' }}>
          {elements.map((element, index) => (
            <DraggableElement key={index} type={element.type}>
              {element.content}
            </DraggableElement>
          ))}
        </div>
      </DropTarget>
    </DndProvider>
  );
};

export default EditorCanvas;
