// src/components/editor-canvas.tsx

import React from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

interface CanvasElement {
  id: string;
  type: string;
  // Add more properties as needed
}

interface EditorCanvasProps {
  elements: CanvasElement[]; // Elements on the canvas
  onDrop: (type: string) => void; // Callback for dropping elements onto the canvas
}

const EditorCanvas: React.FC<EditorCanvasProps> = ({ elements, onDrop }) => {
  const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    // Prevent default behavior to enable dropping
    e.preventDefault();
    // Extract the dropped element type from the data transfer
    const type = e.dataTransfer.getData('type');
    // Call the onDrop callback with the dropped element type
    onDrop(type);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div
        style={{
          width: '100%',
          height: '500px',
          border: '1px solid #ddd',
          padding: '20px',
        }}
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
      >
        {elements.map((element) => (
          <div
            key={element.id}
            style={{
              // Style each element based on its type and other properties
              // You can add more styles or properties as needed
            }}
          >
            {element.type}
          </div>
        ))}
      </div>
    </DndProvider>
  );
};

export default EditorCanvas;
