// src/components/editor/editor-canvas.tsx

import React, { useState } from 'react';
import Element from "../interfaces/element";

interface EditorCanvasProps {
  elements: Element[];
  gridSize: number;
}

const EditorCanvas: React.FC<EditorCanvasProps> = ({ elements, gridSize }) => {
  const [draggingElement, setDraggingElement] = useState<{ id: string; offsetX: number; offsetY: number } | null>(null);

  const handleElementMouseDown = (event: React.MouseEvent<HTMLDivElement>, id: string) => {
    event.preventDefault();
    const { offsetX, offsetY } = event.nativeEvent;
    setDraggingElement({ id, offsetX, offsetY });
  };

  const handleCanvasMouseMove = (event: React.MouseEvent<HTMLDivElement>) => {
    if (draggingElement) {
      const { clientX, clientY } = event;
      const x = Math.round((clientX - draggingElement.offsetX) / gridSize) * gridSize;
      const y = Math.round((clientY - draggingElement.offsetY) / gridSize) * gridSize;
      // Update the position of the dragged element
    }
  };

  const handleCanvasMouseUp = () => {
    setDraggingElement(null);
  };

  return (
    <div
      style={{ width: '100%', height: '500px', border: '1px solid #ddd', padding: '20px' }}
      onMouseMove={handleCanvasMouseMove}
      onMouseUp={handleCanvasMouseUp}
    >
      {elements.map(element => (
        <div
          key={element.id}
          onMouseDown={(event) => handleElementMouseDown(event, element.id)}
          style={{
            position: 'absolute',
            top: element.position.y,
            left: element.position.x,
            width: '50px',
            height: '50px',
            background: '#ccc',
            border: '1px solid #999',
            borderRadius: '4px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            cursor: 'move',
          }}
        >
          {element.id}
        </div>
      ))}
    </div>
  );
};

export default EditorCanvas;
