import React from 'react';
import {useDroppable, DndContext} from '@dnd-kit/core';
import { arrayMove } from '@dnd-kit/sortable';
import DraggableElement from './draggable-element';

interface EditorCanvasProps {
  elements: { id: string; x: number; y: number; type: string }[];
  setElements: React.Dispatch<React.SetStateAction<{ id: string; x: number; y: number; type: string }[]>>;
}

const EditorCanvas: React.FC<EditorCanvasProps> = ({ elements, setElements }) => {
  const {isOver, setNodeRef} = useDroppable({
    id: "editor",
  });

  const handleDragEnd = (event: any) => {
    const { active, over } = event;
    if (!over || active.id === over.id) return;

    const oldIndex = elements.findIndex((element) => element.id === active.id);
    const newIndex = elements.findIndex((element) => element.id === over.id);

    setElements((elements) => arrayMove(elements, oldIndex, newIndex));
  };

  return (
    <DndContext onDragEnd={handleDragEnd}>
      <div
       ref={setNodeRef}
        style={{
          width: '100%',
          height: '500px',
          border: '1px solid #ddd',
          padding: '20px',
          position: 'relative',
        }}
      >
        {elements.map((element) => (
          <div key={element.id} id={element.id}>
            {element.type}
          </div>
        ))}
      </div>
    </DndContext>
  );
};

export default EditorCanvas;
