// src/components/editor.tsx

import React, { useState } from 'react';
import EditorCanvas from '../components/editor-canvas';
import Toolbox from '../components/toolbox';

const App: React.FC = () => {
  const [elements, setElements] = useState<{ id: string; position: { x: number; y: number } }[]>([]);

  const handleElementDrop = (type: string) => {
    // Add the dropped element to the canvas
    const newElement = {
      id: `${type}-${elements.length + 1}`,
      type: type,
      content: `New ${type}`,
      position: { x: 0, y: 0 } // Default position
    };
    setElements(prevElements => [...prevElements, newElement]);
  };

  return (
    <div style={{ display: 'flex' }}>
      <Toolbox onElementDrop={handleElementDrop} />
      <EditorCanvas elements={elements} />
    </div>
  );
};

export default App;
