import React, { useState } from 'react';
import Toolbox from '../components/toolbox';
import EditorCanvas from '../components/editor-canvas';
import { Box, Stack, Grid, Image, Video, Input, Text, Button } from "../elements"

const App: React.FC = () => {
  // State to manage elements on the canvas
  const [elements, setElements] = useState<{ id: string; x: number; y: number; type: string }[]>([]);

  // Define toolbox items
  const toolboxItems = [
    { id: '1', type: 'BOX', content: "<Box />"},
    { id: '2', type: 'STACK', content: "<Stack />"},
    { id: '3', type: 'GRID', content: "<Grid />"},
    { id: '4', type: 'IMAGE', content: "<Image />"},
    { id: '5', type: 'VIDEO', content: "<Video />"},
    { id: '6', type: 'INPUT', content: "<Input />"},
    { id: '7', type: 'TEXT', content: "<Text />"},
    { id: '8', type: 'BUTTON', content: "<Button />"},
    // Add more toolbox items as needed
  ];

  // Callback function to handle dropping toolbox items onto the canvas
  const handleElementDrop = (type: string, x: number, y: number) => {
    const newElement = { id: `${type}-${Date.now()}`, type, x, y };
    setElements((prevElements) => [...prevElements, newElement]);
  };

  return (
    <div style={{ display: 'flex' }}>
      <Toolbox elements={toolboxItems} onElementDrop={handleElementDrop} />
      <EditorCanvas elements={elements} setElements={setElements} />
    </div>
  );
};

export default App;
