import React from 'react';

interface ToolboxProps {
  elements: { id: string; type: string; content: React.ReactNode }[];
  onElementDrop: (type: string, x: number, y: number) => void; // Callback for dropping element onto the canvas
}

const Toolbox: React.FC<ToolboxProps> = ({ elements, onElementDrop }) => {
  const handleDragStart = (type: string, event: React.DragEvent<HTMLDivElement>) => {
    // Set data to be transferred during drag-and-drop
    event.dataTransfer.setData('text/plain', type);
    event.dataTransfer.dropEffect = 'copy'; // Show copy cursor during drag
  };

  const handleDragEnd = (event: React.DragEvent<HTMLDivElement>) => {
    // Reset drag data
    event.dataTransfer.clearData();
  };

  const handleDragOver = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault(); // Allow drop
  };

  return (
    <div
      style={{ display: 'flex', flexDirection: 'column', padding: '10px' }}
      onDragOver={handleDragOver}
      onDragEnd={handleDragEnd}
    >
      {elements.map((element) => (
        <div
          key={element.id}
          draggable
          onDragStart={(event) => handleDragStart(element.type, event)}
          onDrop={(event) => onElementDrop(element.type, event.clientX, event.clientY)}
          style={{ marginBottom: '10px', cursor: 'grab' }}
        >
          {element.content}
        </div>
      ))}
    </div>
  );
};

export default Toolbox;
