import React from 'react';

interface EditorCanvasProps {
  elements: { id: string; type: string; content: React.ReactNode; x: number; y: number }[]; // Include x and y coordinates
  onElementDrop: (type: string, x: number, y: number) => void; // Callback for dropping element onto the canvas
}


const EditorCanvas: React.FC<EditorCanvasProps> = ({ elements, onElementDrop }) => {
  const handleDrop = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault(); // Prevent default drop behavior
    const type = event.dataTransfer.getData('text/plain');
    const x = event.clientX;
    const y = event.clientY;
    onElementDrop(type, x, y);
  };

  return (
    <div
      style={{ width: '100%', height: '500px', border: '1px solid #ddd', padding: '20px' }}
      onDrop={handleDrop}
      onDragOver={(event) => event.preventDefault()} // Allow drop
    >
      {/* Render draggable elements */}
      {elements.map((element) => (
        <div key={element.id} style={{ position: 'absolute', left: `${element.x}px`, top: `${element.y}px` }}>
          {element.content}
        </div>
      ))}
    </div>
  );
};

export default EditorCanvas;
